package acorn2;

public class Customer {
	String id;
	String pw;
	String name;
	
	public Customer() {
		// TODO Auto-generated constructor stub
	}

	public Customer(String id, String pw, String name) {
		super();
		this.id = id;
		this.pw = pw;
		this.name = name;
	}

	public String getId() {
		return id;
	}

	public String getPw() {
		return pw;
	}

	public String getName() {
		return name;
	}
	

}
